interface Item {
    id : string
}

interface List {
    id : string
    text: string
    dragged: boolean
    tasks: Task[]
}

interface Task {
  id : string
        text: string
        drag: boolean
}


export const FindItemIndexById = <T extends Item> (items : T[], id: string) => {
    return items.findIndex((item: T) => item.id === id)
}

export const findIdByDraggedProp = (index: number, state: any) : string  => {
    state.lists[index].tasks.map((task: Task) => {
      if (task.drag === false)
      {
        return task.id
      }  
  })
  return 'd'
  }

  export const findObjectIDByDraggedProp = (lists: List[]) : number => {
    let index 
    let TaskIndex : number = -10
      index = lists.map((list: any) => {
      const Tindex = list.tasks.findIndex((task: Task) => task.drag === true ) 
       if(Tindex > -1) {
         TaskIndex = Tindex
       }
    }) 
     return TaskIndex
  }

  export const findDragStartCol = (lists : List[]) => {
    return lists.findIndex((item) => {
      return item.dragged === true})
  }
