interface Item {
    id : string
}

interface Task {
    id : string
    text: string
    drag: boolean
}


export const FindItemIndexById = <T extends Item> (items : T[], id: string) => {
    return items.findIndex((item: T) => item.id === id)
}

export const findIdByDraggedProp = (index: number, state: any) : string  => {
    state.lists[index].tasks.map((task: Task) => {
      if (task.drag === false)
      {
        return task.id
      }  
  })
  return 'd'
  }

  export const findObjectIDByDraggedProp = (lists: any, index: string ) : number => {
    return lists[index].tasks.findIndex((task: Task) => task.drag === true )
    }
