import React, {useRef} from 'react'
import {CardContainer} from '../styles/styles'
import { useFocus } from './UseFocus'
import { UseAppState } from '../AppStateContect'

const {state, dispatch} = UseAppState()
const hide = {
  opacity: '0'
}
const show = {
  opacity: '1'
}

interface CardProps {
    text : string
    id : string
    hidden: boolean
}

function Card({text, id, hidden}: CardProps ) {
  const inpuRef = useFocus()

  // const handleDrag = (ev: React.DragEvent<HTMLDivElement>) => {

  //   console.log('me hideen',hidden)

  //   }

  // const handleEnd = (ev: React.DragEvent<HTMLDivElement>) => {
  //   console.log('dragend', ev)
  // }
  return (
    <CardContainer style = {hidden? hide:show} 
    id = {id}
    draggable= {true}
    ref={inpuRef} 
    onDragStart={() => {dispatch({type:"DRAGGED_TASK", payload:{taskId: id} })} }>
        {text}
    </CardContainer>
  )
}

export default Card
