import React, { PropsWithChildren, useEffect} from "react";
import "./App.css";
import Column from "./components/Column";
import { AppContainer } from "./styles/styles";
import AddNewItem  from './components/AddNewItem'
import { UseAppState } from './AppStateContect'
import { AppStateContextProps } from "./type";
import { AppState } from './type'



const App = () => {


  state = JSON.parse(localStorage.getItem('list') as string) 
  let {state, dispatch} = UseAppState()
  useEffect(() => {
    let {state, dispatch} = UseAppState()
    localStorage.setItem('list', JSON.stringify(state))
},[state])

  return (
    <div className="App">
      <AppContainer>
        {
          state.lists.map((list, i) => (
            <Column id={list.id} text= {list.text} key = {list.id} index = {i}  />
            ))
        }
        <AddNewItem toggleButtonText={'create new list'} onAdd={text => dispatch({ type: "ADD_LIST", payload:  text })}
/>
      </AppContainer>
    </div>
  );
};

export default App;
