  const inpuRef = useRef()
  const handleDrag = (ev: React.DragEvent<HTMLDivElement>) => {
    console.log('me', inpuRef.current)
  }
  const handleEnd = (ev: React.DragEvent<HTMLDivElement>) => {
    console.log('dragend', ev)
  }
  return (
    <CardContainer draggable= {true} 
    onDragStart={handleDrag} 
    onDragEnd={handleEnd}
    ref={inpuRef.current} >
        {text}
    </CardContainer>
  )
}

export default Card
