import React, { PropsWithChildren} from "react";
import "./App.css";
import Column from "./components/Column";
import { AppContainer } from "./styles/styles";
import AddNewItem  from './components/AddNewItem'
import { UseAppState } from './AppStateContect'



const App = () => {
  const{state, dispatch} = UseAppState()
  return (
    <div className="App">
      <AppContainer>
        {
          state.lists.map((list, i) => (
            <Column id={list.id} text= {list.text} key = {list.id} index = {i} hidden = {list.dragged} /> 
            ))
        }
        <AddNewItem toggleButtonText={'create new list'} onAdd={text => dispatch({ type: "ADD_LIST", payload:  text })}
/>
      </AppContainer>
    </div>
  );
};

export default App;
