import React, { PropsWithChildren } from 'react'
import {ColumnContainer, ColumnTitle} from '../styles/styles'
import AddNewItem from '../components/AddNewItem'
import { UseAppState } from '../AppStateContect'
import Card from './Card'
import {v1 as uuid} from 'uuid'


interface ColumnProps {
    text : string 
    index : number
    id: string
    hidden: boolean
}

export default function Column({text, index, id, hidden} : PropsWithChildren<ColumnProps>) {

  const {state, dispatch} = UseAppState()
  return (
    <ColumnContainer  >
    <ColumnTitle> {text} </ColumnTitle>
    {state.lists[index].tasks.map((task) => {
      return <Card text = {task.text}  
      hidden = {hidden}
      handleDrag = { () => dispatch({type:"DRAGGED_TASK", payload:{taskId: task.id} })  } />
    })}
    <AddNewItem dark toggleButtonText= "+ Add another task" 
    onAdd= {text => dispatch({type:"ADD_TASK", payload:{text, taskId: id} })} />
    </ColumnContainer>
  )
}
