interface Item {
    id : string
}

interface Task {
    id : string
    text: string
    drag: boolean
}


export const FindItemIndexById = <T extends Item> (items : T[], id: string) => {
    return items.findIndex((item: T) => item.id === id)
}

export const findIdByDraggedProp = (index: number, state: any) : string  => {
    state.lists[index].tasks.map((task: Task) => {
      if (task.drag === false)
      {
        return task.id
      }  
  })
  return 'd'
  }

  export const findObjectIDByDraggedProp = (lists: any) : number => {
    let index : number
      index = lists.find((list: any) => {
      console.log('list is', list.tasks)
      const Tindex = list.tasks.findIndex((task: Task) => task.drag === true ) 
      return Tindex > -1
    }) 
     return index
  }
